import {
  makeStyles,
  TableHead,
  Table,
  Paper,
  TableCell,
  TableRow, TableBody
} from "@material-ui/core";
import Axios from 'axios';
import React, { useEffect, useState } from 'react';

const useStyles = makeStyles({
  root: {
    width: "100%",
    margin: "100px 0 0 0 ",
    // backgroundColor: "red",
    textAlign:"center",
    margin:"0 auto"
  },
  paper:{
    textAlign:"center",
    margin:"0 auto",
    width:"800px",
  },
  title: {
    fontSize:"20px",
    padding:"20px 0"
  },
  head:{
    fontSize:"16px",
  }

})

export default function Artikel() {
  const classes = useStyles()
const [article, setArticle] = useState([]);
console.log(article)
  useEffect(() => {
    Axios.get("https://newsapi.org/v2/everything?q=tesla&form=2021-02-28&sortBy=publishedAt&apiKey=156d01b4456c4e86b6e0b9c29791aa68").then(res => {
      setArticle(res.data);
    });
  });

  return <div className={classes.root}>  <Paper className={classes.paper}>
  <h3 className={classes.title}>DATA USER</h3>
  <Table>
    <TableHead>
      <TableRow>
      <TableCell  className={classes.head}>ID</TableCell>

        <TableCell  className={classes.head}>Name</TableCell>
        <TableCell  className={classes.head}>Username</TableCell>
        <TableCell  className={classes.head}>Email</TableCell>
        <TableCell  className={classes.head}>Address</TableCell>
        <TableCell  className={classes.head}>Phone</TableCell>

      </TableRow>
    </TableHead>
    <TableBody>
    {article.map(item => (
      <TableRow>
        <TableCell>{item.id}</TableCell>

        <TableCell>{item.name}</TableCell>
        <TableCell>{item.username}</TableCell>
        <TableCell>{item.email}</TableCell>
        <TableCell>{item.address.street}{" "+item.address.suite+" "}{item.address.city}</TableCell>
        <TableCell>{item.phone}</TableCell>

      </TableRow>
        ))}
    </TableBody>
    <ul>



  </ul>
  </Table>
</Paper></div>;
}
